import React, { useState, useEffect } from 'react';
import './index.css';
import { Contract, BrowserProvider, parseEther } from 'ethers';
import axios from 'axios';

const PresalePage = () => {
  const [bnbPrice, setBnbPrice] = useState(null);
  const [amount, setAmount] = useState(1);
  const [totalTokens, setTotalTokens] = useState(60000);
  const [isConnected, setIsConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState("");

  useEffect(() => {
    axios.get('https://api.coingecko.com/api/v3/simple/price?ids=binancecoin&vs_currencies=usd')
      .then(res => setBnbPrice(res.data.binancecoin.usd))
      .catch(err => console.error(err));
  }, []);

  const connectWallet = async () => {
    if (window.ethereum) {
      const provider = new BrowserProvider(window.ethereum);
      const accounts = await provider.send('eth_requestAccounts', []);
      setWalletAddress(accounts[0]);
      setIsConnected(true);
    } else alert('Install MetaMask');
  };

  const handleAmountChange = (e) => {
    const val = e.target.value;
    setAmount(val);
    setTotalTokens(val * 60000);
  };

  const buyTokens = async () => {
    if (!isConnected) return alert('Connect wallet first');
    const totalBNB = (bnbPrice * amount) / 60;
    const provider = new BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();
    const tokenAddress = "YOUR_TOKEN_CONTRACT_ADDRESS";
    const tokenAbi = ['function buyTokens() external payable'];
    const tokenContract = new Contract(tokenAddress, tokenAbi, signer);

    try {
      const tx = await tokenContract.buyTokens({ value: parseEther(totalBNB.toString()) });
      await tx.wait();
      alert('Success!');
    } catch {
      alert('Failed!');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white p-6 rounded-2xl shadow-xl max-w-md w-full">
        <h1 className="text-3xl font-bold text-indigo-600 mb-4 text-center">Vortrex Token Presale</h1>
        <button
          onClick={connectWallet}
          className="w-full bg-indigo-500 hover:bg-indigo-600 text-white py-2 rounded mb-4"
        >
          {isConnected ? 'Wallet Connected' : 'Connect MetaMask'}
        </button>
        {isConnected && <p className="text-sm text-gray-700 mb-4 break-words">{walletAddress}</p>}
        <input
          type="number"
          value={amount}
          onChange={handleAmountChange}
          className="w-full p-2 border rounded mb-3"
          placeholder="Enter USDT Amount"
        />
        <p className="text-gray-700 mb-1">Total Tokens: <strong>{totalTokens}</strong></p>
        <p className="text-gray-700 mb-3">1 USDT = 60,000 VTX</p>
        {bnbPrice && <p className="text-gray-700 mb-4">BNB Price: ${bnbPrice}</p>}
        <button
          onClick={buyTokens}
          className="w-full bg-green-500 hover:bg-green-600 text-white py-2 rounded"
        >
          Buy Tokens
        </button>
      </div>
    </div>
  );
};

export default PresalePage;
