import React, { useState, useEffect } from 'react';
import './index.css';
import { Contract, BrowserProvider, parseEther } from 'ethers';
import axios from 'axios';

import HomePage from './components/HomePage';
import TokenInfoPage from './components/TokenInfoPage';
import RoadmapPage from './components/RoadmapPage';
import ContactPage from './components/ContactPage';

const App = () => {
  const [bnbPrice, setBnbPrice] = useState(null);
  const [usdtPrice, setUsdtPrice] = useState(null);
  const [amount, setAmount] = useState(1);
  const [totalTokens, setTotalTokens] = useState(60000);
  const [paymentMethod, setPaymentMethod] = useState('USDT');
  const [isConnected, setIsConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState("");

  useEffect(() => {
    axios.get('https://api.coingecko.com/api/v3/simple/price?ids=binancecoin,tether&vs_currencies=usd')
      .then(res => {
        setBnbPrice(res.data.binancecoin.usd);
        setUsdtPrice(res.data.tether.usd);
      })
      .catch(err => console.error(err));
  }, []);

  const connectWallet = async () => {
    if (window.ethereum) {
      const provider = new BrowserProvider(window.ethereum);
      const accounts = await provider.send('eth_requestAccounts', []);
      setWalletAddress(accounts[0]);
      setIsConnected(true);
    } else alert('Install MetaMask');
  };

  const handleAmountChange = (e) => {
    const val = e.target.value;
    setAmount(val);
    setTotalTokens(val * 60000);
  };

  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const buyTokens = async () => {
    if (!isConnected) return alert('Connect wallet first');

    let totalPrice;
    if (paymentMethod === 'BNB') {
      totalPrice = (bnbPrice * amount) / 60;
    } else if (paymentMethod === 'USDT') {
      totalPrice = (usdtPrice * amount) / 60;
    }

    const provider = new BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();
    const tokenAddress = "YOUR_TOKEN_CONTRACT_ADDRESS";
    const tokenAbi = ['function buyTokens() external payable'];
    const tokenContract = new Contract(tokenAddress, tokenAbi, signer);

    try {
      if (paymentMethod === 'BNB') {
        const tx = await tokenContract.buyTokens({ value: parseEther(totalPrice.toString()) });
        await tx.wait();
        alert('Success!');
      } else if (paymentMethod === 'USDT') {
        // You would need to handle the USDT transfer similarly (calling the token contract's transfer function)
        alert('USDT payment logic not implemented.');
      }
    } catch {
      alert('Failed!');
    }
  };

  return (
    <div className="font-sans text-white bg-black">
      <HomePage />
      <div className="min-h-screen bg-gray-900 flex items-center justify-center p-4">
        <div className="bg-gray-800 p-6 rounded-2xl shadow-xl max-w-md w-full">
          <h1 className="text-3xl font-bold text-green-400 mb-4 text-center">Vortrex Token Presale</h1>
          <button
            onClick={connectWallet}
            className="w-full bg-green-500 hover:bg-green-600 text-white py-2 rounded mb-4"
          >
            {isConnected ? 'Wallet Connected' : 'Connect MetaMask'}
          </button>
          {isConnected && <p className="text-sm text-gray-300 mb-4 break-words">{walletAddress}</p>}
          <input
            type="number"
            value={amount}
            onChange={handleAmountChange}
            className="w-full p-2 border rounded mb-3 bg-gray-700"
            placeholder="Enter Amount"
          />
          <p className="text-gray-300 mb-1">Total Tokens: <strong>{totalTokens}</strong></p>
          <p className="text-gray-300 mb-3">1 USDT = 60,000 VTX</p>

          {bnbPrice && usdtPrice && (
            <div className="flex items-center gap-2 mb-3">
              <img
                src="https://media-hosting.imagekit.io/a72cc4b9f8a54b6b/binance-coin-bnb-logo.png?Expires=1841002214&Key-Pair-Id=K2ZIVPTIP2VGHC&Signature=gQi-OOv7BHzs2XD7ChPQO5HHoNTA5PALMw56A~LQlls9vbF25DFCgKGq2whvo7NK--i2TGg3URlCsx4mQR9LKzGOgv3mLAOmjWs900QLW22RCYJqRNz7KQ6FaqVEncTxpXtofPgtiXc3RmQPzD1c7Y8pS97lJey0-uIJsXv-fgKj5qDAg1b1Q-m1-vrh-4oYLfipYBuxz1ldNp5SUGD~PdDAx6ALhXtyMO7uwnwWeEzOMuKLM409PTYs2FUqYrdeWiYvBceQQvdW-7oDLwjfooYGlxQJPgunjKqZrlNU9h6r5sEzJMvyrBon-~O0dL3cGR473VW2nI00n834TbhBFA__"
                alt="BNB Logo"
                className="w-8 h-8"
              />
              <p className="text-gray-300">BNB Price: ${bnbPrice}</p>
            </div>
          )}

          {usdtPrice && (
            <div className="flex items-center gap-2 mb-4">
              <img
                src="https://cryptologos.cc/logos/tether-usdt-logo.png"
                alt="USDT Logo"
                className="w-8 h-8"
              />
              <p className="text-gray-300">USDT Price: ${usdtPrice}</p>
            </div>
          )}

          {/* Dropdown to select payment method */}
          <div className="mb-4">
            <label className="text-gray-300">Select Payment Method:</label>
            <select
              onChange={handlePaymentMethodChange}
              value={paymentMethod}
              className="w-full p-2 border rounded mb-3 bg-gray-700"
            >
              <option value="USDT">USDT</option>
              <option value="BNB">BNB</option>
            </select>
          </div>

          <button
            onClick={buyTokens}
            className="w-full bg-green-500 hover:bg-green-600 text-white py-2 rounded"
          >
            Buy Tokens
          </button>
        </div>
      </div>
      <TokenInfoPage />
      <RoadmapPage />
      <ContactPage />
    </div>
  );
};

export default App;
