import React, { useState, useEffect } from 'react';
import './index.css';
import { Contract, BrowserProvider, parseEther, parseUnits } from 'ethers';
import axios from 'axios';

import HomePage from './components/HomePage';
import TokenInfoPage from './components/TokenInfoPage';
import RoadmapPage from './components/RoadmapPage';
import ContactPage from './components/ContactPage';

const App = () => {
  const [bnbPrice, setBnbPrice] = useState(null);
  const [usdtPrice, setUsdtPrice] = useState(null);
  const [amount, setAmount] = useState(1);
  const [totalTokens, setTotalTokens] = useState(60000);
  const [paymentMethod, setPaymentMethod] = useState('USDT');
  const [isConnected, setIsConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState("");

  const USDT_ADDRESS = "0x55d398326f99059fF775485246999027B3197955";
  const PRESALE_CONTRACT = "YOUR_PRESALE_CONTRACT_ADDRESS";
  const USDT_ABI = [
    "function approve(address spender, uint256 amount) public returns (bool)"
  ];
  const PRESALE_ABI = [
    "function buyTokens() external payable",
    "function buyTokensWithUSDT(uint256 amount) public"
  ];

  useEffect(() => {
    axios.get('https://api.coingecko.com/api/v3/simple/price?ids=binancecoin,tether&vs_currencies=usd')
      .then(res => {
        setBnbPrice(res.data.binancecoin.usd);
        setUsdtPrice(res.data.tether.usd);
      })
      .catch(err => console.error(err));
  }, []);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const provider = new BrowserProvider(window.ethereum);
        const accounts = await provider.send('eth_requestAccounts', []);
        if (accounts.length > 0) {
          setWalletAddress(accounts[0]);
          setIsConnected(true);
          alert('MetaMask Connected!');
        } else {
          alert('No accounts found. Please check your MetaMask extension.');
        }
      } catch (error) {
        console.error('Error connecting to MetaMask:', error);
        alert('Error connecting to MetaMask. Please try again later.');
      }
    } else {
      alert('MetaMask not detected. Please install MetaMask extension in your browser.');
    }
  };

  const handleAmountChange = (e) => {
    const val = e.target.value;
    setAmount(val);
    setTotalTokens(val * 60000);
  };

  const handlePaymentMethodChange = (method) => {
    setPaymentMethod(method);
  };

  const buyTokens = async () => {
    if (!isConnected) return alert('Connect wallet first');
    const provider = new BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();

    const totalUsd = amount;
    const totalInBNB = (bnbPrice && paymentMethod === 'BNB') ? (totalUsd / bnbPrice) : 0;
    const presale = new Contract(PRESALE_CONTRACT, PRESALE_ABI, signer);

    try {
      if (paymentMethod === 'BNB') {
        const tx = await presale.buyTokens({ value: parseEther(totalInBNB.toString()) });
        await tx.wait();
        alert('BNB Payment Success!');
      } else if (paymentMethod === 'USDT') {
        const usdt = new Contract(USDT_ADDRESS, USDT_ABI, signer);
        const amountInUSDT = parseUnits(totalUsd.toString(), 18);
        const approveTx = await usdt.approve(PRESALE_CONTRACT, amountInUSDT);
        await approveTx.wait();
        const buyTx = await presale.buyTokensWithUSDT(amountInUSDT);
        await buyTx.wait();
        alert('USDT Payment Success!');
      }
    } catch (err) {
      console.error(err);
      alert('Transaction Failed');
    }
  };

  return (
    <div className="font-sans text-white bg-black">
      <HomePage />
      <div className="min-h-screen bg-black flex items-center justify-center p-6">
        <div className="bg-gradient-to-r from-purple-600 via-pink-500 to-red-500 p-1 rounded-3xl shadow-2xl max-w-md w-full">
          <div className="bg-gray-900 p-6 rounded-3xl">
            <h1 className="text-3xl font-bold text-yellow-400 mb-4 text-center">Vortrex Token Presale</h1>

            <button
              onClick={connectWallet}
              className="w-full bg-gradient-to-r from-purple-600 to-pink-500 hover:scale-105 transition-transform text-white py-2 rounded mb-4"
            >
              {isConnected ? 'Wallet Connected' : 'Connect MetaMask'}
            </button>

            {isConnected && <p className="text-sm text-gray-300 mb-4 break-words">{walletAddress}</p>}

            <input
              type="number"
              value={amount}
              onChange={handleAmountChange}
              className="w-full p-2 border border-yellow-400 rounded bg-gray-800 text-white mb-3"
              placeholder="Enter Amount"
            />
            <p className="text-gray-300 mb-1">Total Tokens: <strong>{totalTokens}</strong></p>
            <p className="text-gray-300 mb-3">1 USDT = 60,000 VTX</p>

            <div className="flex items-center gap-2 mb-3">
              <img src="/images/bnb-logo.png" alt="BNB Logo" className="w-8 h-8" />
              <p className="text-gray-300">BNB - BEP-20</p>
            </div>

            <div className="flex items-center gap-2 mb-4">
              <img src="/images/usdt-logo.png" alt="USDT Logo" className="w-8 h-8" />
              <p className="text-gray-300">USDT BEP-20</p>
            </div>

            <div className="mb-4">
              <label className="text-gray-300 block mb-2">Select Payment Method:</label>
              <div className="flex gap-4">
                <button
                  onClick={() => handlePaymentMethodChange('USDT')}
                  className={`flex items-center gap-2 px-4 py-2 rounded-full w-full ${paymentMethod === 'USDT' ? 'bg-gradient-to-r from-purple-600 to-pink-500' : 'bg-gray-800'} hover:scale-105 transition-transform text-white`}
                >
                  <img src="/images/usdt-logo.png" alt="USDT" className="w-6 h-6" /> USDT
                </button>
                <button
                  onClick={() => handlePaymentMethodChange('BNB')}
                  className={`flex items-center gap-2 px-4 py-2 rounded-full w-full ${paymentMethod === 'BNB' ? 'bg-gradient-to-r from-purple-600 to-pink-500' : 'bg-gray-800'} hover:scale-105 transition-transform text-white`}
                >
                  <img src="/images/bnb-logo.png" alt="BNB" className="w-6 h-6" /> BNB
                </button>
              </div>
            </div>

            <button
              onClick={buyTokens}
              className="w-full bg-gradient-to-r from-purple-600 to-pink-500 hover:scale-105 transition-transform text-white py-2 rounded"
            >
              Buy Tokens
            </button>
          </div>
        </div>
      </div>

      {/* How to Buy Section */}
      <div className="bg-gray-800 p-6 mt-6 rounded-3xl">
        <h2 className="text-2xl font-bold text-yellow-400 mb-6 text-center">How to Buy Vortrex Tokens</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">

          <div className="bg-gradient-to-r from-orange-500 via-pink-500 to-purple-600 p-6 rounded-xl text-center relative overflow-hidden shadow-lg hover:scale-105 transform transition-all duration-300">
            <img src="/images/wallet.png" alt="Wallet Icon" className="mx-auto w-16 h-16 mb-4 z-10 relative" />
            <h3 className="text-xl font-bold text-white z-10 mb-3">Step 1: Connect Your Wallet</h3>
            <p className="text-white z-10">To begin, click the "Connect MetaMask" button. This will allow you to connect your MetaMask wallet and interact with the presale.</p>
          </div>

          <div className="bg-gradient-to-r from-orange-500 via-pink-500 to-purple-600 p-6 rounded-xl text-center relative overflow-hidden shadow-lg hover:scale-105 transform transition-all duration-300">
            <img src="/images/usdt.png" alt="USDT Icon" className="mx-auto w-16 h-16 mb-4 z-10 relative" />
            <h3 className="text-xl font-bold text-white z-10 mb-3">Step 2: Buy with USDT</h3>
            <p className="text-white z-10">Enter the amount of Vortrex tokens you wish to purchase, select USDT as your payment method, and approve the transaction. Your tokens will be sent directly to your wallet after the payment is confirmed.</p>
          </div>

