import React from 'react';

const TokenInfoPage = () => {
  return (
    <div className="bg-gradient-to-b from-black via-gray-900 to-gray-800 text-white min-h-screen">
      {/* Header Section */}
      <header className="text-center p-10">
        <h1 className="text-5xl font-extrabold text-[#00FF9F]">Vortrex Token (VTX)</h1>
        <p className="mt-4 text-xl text-gray-300">The future of decentralized finance is here!</p>
      </header>

      {/* Token Details Section */}
      <section className="py-10 text-center">
        <h2 className="text-3xl font-semibold text-[#00FF9F]">Token Details</h2>
        <ul className="mt-4 text-gray-300">
          <li>Symbol: VTX</li>
          <li>Total Supply: 1,000,000,000 VTX</li>
          <li>Blockchain: Binance Smart Chain (BEP-20)</li>
        </ul>

        {/* Image below token details */}
        <div className="mt-10">
          <img 
            src="/images/vtx_tokenomics.png" 
            alt="VTX Tokenomics"
            className="mx-auto w-[600px] h-[350px] object-contain rounded-xl shadow-lg"
          />
        </div>
      </section>

      {/* Use Cases Section */}
      <section className="py-10">
        <h2 className="text-3xl font-semibold text-[#00FF9F] text-center">Use Cases</h2>
        <p className="mt-4 text-center text-gray-300">
          VTX is designed to be used for staking, governance, and as a utility token within the Vortrex ecosystem.
        </p>
      </section>

      {/* Purchase Button Section */}
      <section className="text-center py-10">
        <button className="bg-gradient-to-r from-pink-600 to-purple-600 text-white px-6 py-3 rounded-full shadow-md hover:scale-105 transition-transform">
          Purchase VTX Token
        </button>
      </section>
    </div>
  );
};

export default TokenInfoPage;
