import React, { useState } from 'react';
import { FaShieldAlt, FaRocket, FaCogs, FaBars, FaTimes } from 'react-icons/fa';

const HomePage = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <div className="font-sans text-white bg-black">

      {/* Header */}
      <header className="fixed top-0 left-0 w-full z-50 bg-black bg-opacity-80 backdrop-blur-md shadow-md">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex justify-between items-center h-16">
          <div className="text-2xl font-bold text-green-400">Vortrex</div>
          <div className="hidden md:flex space-x-6 text-white text-base">
            <a href="#hero" className="hover:text-green-400 transition">Home</a>
            <a href="#features" className="hover:text-green-400 transition">Features</a>
            <a href="#presale" className="hover:text-green-400 transition">Presale</a>
            <a href="#roadmap" className="hover:text-green-400 transition">Roadmap</a>
            <a href="#contact" className="hover:text-green-400 transition">Contact</a>
          </div>
          <div className="md:hidden">
            <button
              onClick={() => setMenuOpen(!menuOpen)}
              className="text-white text-xl focus:outline-none"
            >
              {menuOpen ? <FaTimes /> : <FaBars />}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {menuOpen && (
          <div className="md:hidden bg-black text-white px-4 py-2 space-y-2">
            <a href="#hero" className="block hover:text-green-400">Home</a>
            <a href="#features" className="block hover:text-green-400">Features</a>
            <a href="#presale" className="block hover:text-green-400">Presale</a>
            <a href="#roadmap" className="block hover:text-green-400">Roadmap</a>
            <a href="#contact" className="block hover:text-green-400">Contact</a>
          </div>
        )}
      </header>

      {/* Add spacing for fixed header */}
      <div className="h-16"></div>

      {/* Your existing sections start here */}
      {/* Hero Section */}
      <section id="hero" className="relative h-screen ...">
        {/* Rest of the code remains unchanged */}
      </section>

      {/* Features Section */}
      <section id="features" className="py-20 ...">
        {/* ... */}
      </section>

      {/* Presale Section */}
      <section id="presale" className="py-20 ...">
        {/* ... */}
      </section>

      {/* Roadmap Section */}
      <section id="roadmap" className="py-20 ...">
        {/* ... */}
      </section>

      {/* Contact Section */}
      <section id="contact" className="py-20 ...">
        {/* ... */}
      </section>
    </div>
  );
};

export default HomePage;
