import React, { useState, useEffect } from 'react';
import './index.css';
import { Contract, BrowserProvider, parseEther, parseUnits } from 'ethers';
import axios from 'axios';

import HomePage from './components/HomePage';
import TokenInfoPage from './components/TokenInfoPage';
import RoadmapPage from './components/RoadmapPage';
import ContactPage from './components/ContactPage';

const App = () => {
  const [bnbPrice, setBnbPrice] = useState(null);
  const [usdtPrice, setUsdtPrice] = useState(null);
  const [amount, setAmount] = useState(1);
  const [totalTokens, setTotalTokens] = useState(69000);
  const [paymentMethod, setPaymentMethod] = useState('USDT');
  const [isConnected, setIsConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState('');

  const USDT_ADDRESS = '0x55d398326f99059fF775485246999027B3197955';
  const PRESALE_CONTRACT = '0x09Ff9D31fdFaAE455Cc6d8dB28C022B1057Abdfb';

  const USDT_ABI = [
    'function approve(address spender, uint256 amount) public returns (bool)',
  ];

  const PRESALE_ABI = [
    'function buyWithBNB() external payable',
    'function buyWithUSDT(uint256 amount) external',
  ];

  useEffect(() => {
    axios
      .get('https://api.coingecko.com/api/v3/simple/price?ids=binancecoin,tether&vs_currencies=usd')
      .then((res) => {
        setBnbPrice(res.data.binancecoin.usd);
        setUsdtPrice(res.data.tether.usd);
        console.log('BNB Price Loaded:', res.data.binancecoin.usd); // Debug log
      })
      .catch((err) => console.error(err));
  }, []);

  useEffect(() => {
    if (paymentMethod === 'USDT') {
      setTotalTokens(amount * 69000); // 1 USDT = 69000 VTX
    } else if (paymentMethod === 'BNB' && bnbPrice) {
      const tokensPerBNB = bnbPrice * 69000;
      const tokens = (amount / bnbPrice) * tokensPerBNB; // amount is in USD
      setTotalTokens(tokens);
    }
  }, [amount, paymentMethod, bnbPrice]);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const provider = new BrowserProvider(window.ethereum);
        const accounts = await provider.send('eth_requestAccounts', []);
        if (accounts.length > 0) {
          setWalletAddress(accounts[0]);
          setIsConnected(true);
          alert('MetaMask Connected!');
        } else {
          alert('No accounts found.');
        }
      } catch (error) {
        console.error('Connection error:', error);
        alert('Error connecting to MetaMask.');
      }
    } else {
      alert('MetaMask not detected.');
    }
  };

  const handleAmountChange = (e) => {
    const val = e.target.value;
    setAmount(val);
  };

  const handlePaymentMethodChange = (method) => {
    setPaymentMethod(method);
  };

  const buyTokens = async () => {
    if (!isConnected) return alert('Connect wallet first');

    const provider = new BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();
    const presale = new Contract(PRESALE_CONTRACT, PRESALE_ABI, signer);

    const totalUsd = amount;
    const tokensToBuy = totalUsd * 69000;

    try {
      if (paymentMethod === 'BNB') {
        if (!bnbPrice) return alert('BNB price not loaded');
        const totalInBNB = totalUsd / bnbPrice;
        const tx = await presale.buyWithBNB({
          value: parseUnits(totalInBNB.toString(), 18), // Use 18 decimals for BNB
        });
        await tx.wait();
        alert(`BNB Payment Success! You bought ${tokensToBuy} VTX`);
      } else if (paymentMethod === 'USDT') {
        const usdt = new Contract(USDT_ADDRESS, USDT_ABI, signer);
        const amountInUSDT = parseUnits(totalUsd.toString(), 18);
        const approveTx = await usdt.approve(PRESALE_CONTRACT, amountInUSDT);
        await approveTx.wait();

        const buyTx = await presale.buyWithUSDT(amountInUSDT);
        await buyTx.wait();
        alert(`USDT Payment Success! You bought ${tokensToBuy} VTX`);
      }
    } catch (err) {
      console.error('Error with the transaction:', err);
      alert('Transaction Failed');
    }
  };

  return (
    <div className="font-sans text-white bg-black">
      <HomePage />
      <section className="bg-gray-900 py-12 px-4 md:px-6">
        <div className="max-w-4xl mx-auto bg-gradient-to-r from-purple-600 via-pink-500 to-red-500 p-1 rounded-3xl shadow-lg">
          <div className="bg-gray-900 rounded-3xl p-6 md:p-8">
            <div className="flex items-center gap-4 mb-6">
              <img src="/images/coin-icon.png" alt="FDR Coin" className="w-16 h-16" />
              <div>
                <p className="text-lg font-semibold text-white">VTX = 0.000014</p>
                <p className="text-green-400 text-sm">+10% Next Price</p>
                <p className="text-yellow-400 text-sm">Listing VTX = $0.000029</p>
              </div>
            </div>

            <div className="relative my-6 h-12">
              <div className="absolute top-1/2 transform -translate-y-1/2 w-full h-3 bg-purple-900 rounded-full"></div>
              <div className="absolute top-1/2 transform -translate-y-1/2 h-3 bg-orange-500 rounded-full" style={{ width: '50%' }}></div>
              <div className="absolute left-[50%] top-1/2 transform -translate-x-1/2 -translate-y-1/2">
                <img src="/images/coin-icon.png" alt="Token Logo" className="w-10 h-10 rounded-full shadow-xl border-4 border-white bg-white" />
              </div>
            </div>

            <h2 className="text-3xl font-extrabold text-yellow-400 text-center mb-6">Vortrex Token Presale</h2>

            <button
              onClick={connectWallet}
              className="w-full mb-4 py-2 rounded text-white font-medium bg-gradient-to-r from-purple-600 to-pink-500 hover:scale-105 transition-transform"
            >
              {isConnected ? 'Wallet Connected' : 'Connect MetaMask'}
            </button>

            {isConnected && <p className="text-sm text-gray-400 mb-4 text-center break-words">{walletAddress}</p>}

            <p className="text-white font-medium mb-2">Step 1 - Select the payment method</p>
            <div className="flex justify-between items-center gap-3 mb-4">
              <button
                onClick={() => handlePaymentMethodChange('USDT')}
                className={`flex-1 px-4 py-2 rounded-full text-white flex flex-col items-center justify-center gap-1 text-sm ${paymentMethod === 'USDT' ? 'bg-gradient-to-r from-purple-600 to-pink-500' : 'bg-gray-800'} transition-transform hover:scale-105`}
              >
                <img src="/images/usdt.png" alt="USDT" className="w-5 h-5" />
                <span className="text-base">USDT</span>
                <span className="text-xs text-gray-300">BEP-20</span>
              </button>
              <button
                onClick={() => handlePaymentMethodChange('BNB')}
                className={`flex-1 px-4 py-2 rounded-full text-white flex flex-col items-center justify-center gap-1 text-sm ${paymentMethod === 'BNB' ? 'bg-gradient-to-r from-purple-600 to-pink-500' : 'bg-gray-800'} transition-transform hover:scale-105`}
              >
                <img src="/images/bnb.png" alt="BNB" className="w-5 h-5" />
                <span className="text-base">BNB</span>
                <span className="text-xs text-gray-300">BEP-20</span>
              </button>
            </div>

            <input
              type="number"
              value={amount}
              onChange={handleAmountChange}
              className="w-full px-4 py-2 mb-3 bg-gray-800 text-white border border-yellow-400 rounded focus:outline-none"
              placeholder="Enter Amount (in USD)"
            />

            <p className="text-gray-300 mb-1 text-sm">
              {paymentMethod === 'USDT'
                ? "1 USDT = 69,000 VTX"
                : `1 BNB = ${bnbPrice ? Math.floor(bnbPrice * 69000).toLocaleString() : 0} VTX`}
            </p>

            <div className="bg-gray-800 text-sm text-center text-green-400 font-medium p-2 mt-3 rounded-lg">
              Potential Profit: 2X<br />
              <span className="text-xs text-gray-400">*Based on listing price of $0.000029. Not guaranteed.</span>
            </div>

            <button
              onClick={buyTokens}
              className="mt-6 w-full py-2 rounded bg-gradient-to-r from-purple-600 to-pink-500 text-white font-semibold hover:scale-105 transition-transform"
            >
              Buy Tokens
            </button>
          </div>
        </div>
      </section>

      <TokenInfoPage />
      <RoadmapPage />
      <ContactPage />
    </div>
  );
};

export default App;
