import React, { useState, useEffect } from 'react';
import Web3 from 'web3';
import presaleAbi from '../abi/presaleAbi.json';
import tokenAbi from '../abi/tokenAbi.json';

const presaleAddress = '0x...'; // replace with actual presale contract address
const tokenAddress = '0x...';   // replace with actual token contract address

const BuySection = () => {
  const [web3, setWeb3] = useState(null);
  const [account, setAccount] = useState('');
  const [amount, setAmount] = useState('1'); // Default to 1 USDT/BNB
  const [paymentMethod, setPaymentMethod] = useState('USDT');
  const [bnbPrice, setBnbPrice] = useState(0);
  const [totalTokens, setTotalTokens] = useState(0);
  const [status, setStatus] = useState('');

  useEffect(() => {
    if (paymentMethod === 'BNB') {
      fetchBNBPrice();
    }
  }, [paymentMethod]);

  useEffect(() => {
    const amt = parseFloat(amount || '1');
    if (paymentMethod === 'USDT') {
      setTotalTokens(amt * 69000); // 1 USDT = 69000 VTX
    } else if (paymentMethod === 'BNB' && bnbPrice) {
      const tokens = (amt / bnbPrice) * (bnbPrice * 69000);
      setTotalTokens(tokens);
    }
  }, [amount, paymentMethod, bnbPrice]);

  const fetchBNBPrice = async () => {
    try {
      const response = await fetch('https://api.binance.com/api/v3/ticker/price?symbol=BNBUSDT');
      const data = await response.json();
      setBnbPrice(parseFloat(data.price));
    } catch (error) {
      console.error('Failed to fetch BNB price:', error);
    }
  };

  const connectWallet = async () => {
    if (window.ethereum) {
      const web3Instance = new Web3(window.ethereum);
      await window.ethereum.request({ method: 'eth_requestAccounts' });
      const accounts = await web3Instance.eth.getAccounts();
      setWeb3(web3Instance);
      setAccount(accounts[0]);
    } else {
      alert('Please install MetaMask to use this feature.');
    }
  };

  const handleAmountChange = (e) => {
    const val = e.target.value;
    if (parseFloat(val) < 1 || val === '') return;
    setAmount(val);
  };

  const buyTokens = async () => {
    if (!web3 || !account) {
      alert('Connect your wallet first.');
      return;
    }

    const presaleContract = new web3.eth.Contract(presaleAbi, presaleAddress);
    const tokenAmount = parseInt(totalTokens * 10 ** 18);

    try {
      if (paymentMethod === 'USDT') {
        const usdt = new web3.eth.Contract(tokenAbi, '0x55d398326f99059fF775485246999027B3197955'); // USDT (BEP-20)
        const usdtAmount = web3.utils.toWei(amount, 'ether');

        await usdt.methods.approve(presaleAddress, usdtAmount).send({ from: account });
        await presaleContract.methods.buyWithUSDT(usdtAmount).send({ from: account });
      } else {
        const bnbAmount = web3.utils.toWei(amount, 'ether');
        await presaleContract.methods.buyWithBNB().send({ from: account, value: bnbAmount });
      }

      setStatus('Purchase successful!');
    } catch (err) {
      console.error(err);
      setStatus('Transaction failed.');
    }
  };

  return (
    <section className="bg-[#0f172a] text-white py-10 px-4 sm:px-8 lg:px-24 rounded-xl shadow-lg">
      <div className="max-w-2xl mx-auto text-center">
        <h2 className="text-3xl font-bold text-yellow-400 mb-4">Vortrex Token Presale</h2>
        <p className="mb-6 text-gray-300">Buy VTX tokens using USDT or BNB during our limited presale.</p>

        <div className="mb-4">
          <label className="block text-left mb-1 font-semibold">Select Payment Method:</label>
          <select
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
            className="w-full p-2 bg-gray-800 text-white border border-yellow-400 rounded"
          >
            <option value="USDT">USDT (BEP-20)</option>
            <option value="BNB">BNB</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block text-left mb-1 font-semibold">Enter Amount ({paymentMethod}):</label>
          <input
            type="number"
            min="1"
            value={amount}
            onChange={handleAmountChange}
            className="w-full px-4 py-2 bg-gray-800 text-white border border-yellow-400 rounded focus:outline-none"
            placeholder="Enter Amount"
          />
        </div>

        <div className="mb-4">
          <label className="block text-left mb-1 font-semibold">You Will Receive:</label>
          <p className="text-lg font-bold text-green-400">{totalTokens.toLocaleString()} VTX</p>
        </div>

        {!account ? (
          <button
            onClick={connectWallet}
            className="w-full py-3 bg-yellow-400 hover:bg-yellow-500 text-black font-bold rounded-lg transition duration-300"
          >
            Connect Wallet
          </button>
        ) : (
          <button
            onClick={buyTokens}
            className="w-full py-3 bg-green-500 hover:bg-green-600 text-white font-bold rounded-lg transition duration-300"
          >
            Buy Tokens
          </button>
        )}

        {status && <p className="mt-4 text-sm text-center text-yellow-300">{status}</p>}
      </div>
    </section>
  );
};

export default BuySection;
