import React from 'react';

const HomePage = () => {
  return (
    <div className="bg-blue-900 text-white min-h-screen">
      {/* Header Section */}
      <header className="text-center p-10">
        <h1 className="text-4xl font-bold">Vortrex Token Presale</h1>
        <p className="mt-4 text-xl">Join the presale now and be part of the future of crypto!</p>
        <button className="mt-6 bg-yellow-500 px-6 py-2 rounded-full">Join Now</button>
      </header>

      {/* How to Participate Section */}
      <section className="py-10">
        <h2 className="text-3xl text-center">How to Participate</h2>
        <ul className="mt-4 text-center">
          <li>Step 1: Connect your wallet</li>
          <li>Step 2: Choose your preferred payment method (BNB or USDT)</li>
          <li>Step 3: Buy Vortrex tokens and join the community!</li>
        </ul>
      </section>
    </div>
  );
};

export default HomePage;
