import React, { useState } from 'react';
import { FaShieldAlt, FaRocket, FaCogs, FaBars, FaTimes } from 'react-icons/fa';

const HomePage = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <div className="font-sans text-white bg-black">

      {/* Header */}
      <header className="fixed top-0 left-0 w-full z-50 bg-black bg-opacity-90 backdrop-blur-md shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 md:px-10 flex justify-between items-center h-16">
          <div className="text-2xl font-bold text-green-400">Vortrex</div>

          {/* Desktop Menu */}
          <nav className="hidden md:flex space-x-4 text-white text-base">
            {['Home', 'Features', 'Presale', 'Roadmap', 'Contact'].map((item, i) => (
              <a
                key={i}
                href={`#${item.toLowerCase()}`}
                className="px-4 py-2 rounded-full bg-gray-800 hover:bg-green-500 hover:text-black transition duration-300"
              >
                {item}
              </a>
            ))}
          </nav>

          {/* Mobile Menu Toggle */}
          <div className="md:hidden">
            <button
              onClick={() => setMenuOpen(!menuOpen)}
              className="text-white text-xl focus:outline-none"
            >
              {menuOpen ? <FaTimes /> : <FaBars />}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {menuOpen && (
          <div className="md:hidden bg-black px-6 py-6 space-y-4 rounded-b-xl shadow-lg border-t border-gray-700">
            {['Home', 'Features', 'Presale', 'Roadmap', 'Contact'].map((item, i) => (
              <a
                key={i}
                href={`#${item.toLowerCase()}`}
                className="block text-white px-4 py-2 rounded-lg bg-gray-800 hover:bg-green-500 hover:text-black transition duration-300"
              >
                {item}
              </a>
            ))}
          </div>
        )}
      </header>

      {/* Offset for fixed header */}
      <div className="h-16"></div>

      {/* Hero Section */}
      <section
        id="hero"
        className="relative min-h-screen bg-cover bg-center flex items-center justify-center text-center px-4 sm:px-6 md:px-10"
        style={{
          backgroundImage:
            'url("https://images.unsplash.com/photo-1606787627690-2d5107a74138?auto=format&fit=crop&w=1080&q=80")',
        }}
        aria-hidden="true"
      >
        <div className="absolute inset-0 bg-black bg-opacity-60"></div>
        <div className="relative z-10 w-full max-w-2xl mx-auto">
          <h1 className="text-3xl sm:text-4xl md:text-5xl font-bold mb-4 animate__animated animate__fadeIn leading-tight">
            Revolutionizing the Future of Crypto
          </h1>
          <p className="text-base sm:text-lg md:text-xl mb-6 animate__animated animate__fadeIn animate__delay-1s">
            Join the Vortrex Presale and be a part of the future today.
          </p>
          <button className="bg-green-500 hover:bg-green-600 px-5 py-3 text-base sm:text-lg rounded-full shadow-md transition duration-300">
            Buy Tokens Now
          </button>
        </div>
      </section>

      {/* Features Section */}
      <section
        id="features"
        className="py-20 bg-gray-900 bg-cover px-4 sm:px-6 md:px-10"
        style={{
          backgroundImage:
            'url("https://images.unsplash.com/photo-1604446924372-13eaa2b2822d?auto=format&fit=crop&w=1080&q=80")',
        }}
        aria-hidden="true"
      >
        <div className="max-w-7xl mx-auto text-center">
          <h2 className="text-3xl sm:text-4xl font-semibold mb-12">Why Choose Vortrex?</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 sm:gap-8">
            {[{
              Icon: FaShieldAlt,
              title: 'Security First',
              desc: 'Complete security with the highest encryption standards.',
            }, {
              Icon: FaRocket,
              title: 'Fast Transactions',
              desc: 'Instant transfers with minimal fees and no delays.',
            }, {
              Icon: FaCogs,
              title: 'Decentralized',
              desc: 'A truly decentralized platform with no middlemen.',
            }].map(({ Icon, title, desc }, i) => (
              <div
                key={i}
                className="bg-gray-800 p-6 rounded-xl shadow-md hover:shadow-xl transition duration-300"
              >
                <Icon className="text-3xl sm:text-4xl mb-4 text-green-400 mx-auto" />
                <h3 className="text-xl sm:text-2xl font-semibold mb-2">{title}</h3>
                <p className="text-gray-300 text-sm sm:text-base">{desc}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Presale Section */}
      <section
        id="presale"
        className="py-20 bg-gray-950 bg-cover px-4 sm:px-6 md:px-10"
        style={{
          backgroundImage:
            'url("https://images.unsplash.com/photo-1606787627740-07f3b3b5d28c?auto=format&fit=crop&w=1080&q=80")',
        }}
        aria-hidden="true"
      >
        <div className="max-w-3xl mx-auto text-center">
          <h2 className="text-3xl sm:text-4xl font-semibold mb-6">Join the Presale</h2>
          <p className="text-base sm:text-lg text-gray-300 mb-8">
            Get early access to Vortrex tokens. Limited time offer!
          </p>
          <div className="bg-gray-800 p-6 rounded-xl shadow-md">
            <div className="w-full h-2 bg-gray-600 rounded-full overflow-hidden mb-4">
              <div className="h-full bg-green-500 w-3/4 rounded-full"></div>
            </div>
            <p className="text-sm text-gray-400 mb-1">75% of tokens sold</p>
            <p className="text-sm text-gray-400 mb-4">10 days remaining</p>
            <button className="bg-green-500 hover:bg-green-600 px-5 py-3 text-base rounded-full transition duration-300">
              Buy Now
            </button>
          </div>
        </div>
      </section>

      {/* Roadmap Section */}
      <section id="roadmap" className="py-20 bg-gray-900 px-4 sm:px-6 md:px-10">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-3xl sm:text-4xl font-semibold mb-10">Our Roadmap</h2>
          {[{
            quarter: 'Q1 2025',
            desc: 'Launch of Vortrex Token and Initial Exchange Listing'
          }, {
            quarter: 'Q2 2025',
            desc: 'Expansion of Community Partnerships and Staking Platform'
          }, {
            quarter: 'Q3 2025',
            desc: 'Launch of DApp for Staking, Rewards, and Governance'
          }].map((item, index) => (
            <div
              key={index}
              className="bg-gray-800 p-5 rounded-lg shadow mb-6 text-left"
            >
              <h3 className="text-lg sm:text-xl text-green-400 font-semibold">{item.quarter}</h3>
              <p className="text-gray-300 text-sm sm:text-base">{item.desc}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Contact Section */}
      <section
        id="contact"
        className="py-20 bg-cover bg-center px-4 sm:px-6 md:px-10"
        style={{
          backgroundImage:
            'url("https://images.unsplash.com/photo-1581134976555-0e91b2f97a94?auto=format&fit=crop&w=1080&q=80")',
        }}
        aria-hidden="true"
      >
        <div className="max-w-3xl mx-auto text-center">
          <h2 className="text-3xl sm:text-4xl font-semibold mb-6">Get In Touch</h2>
          <p className="text-base sm:text-lg text-gray-300 mb-4">
            Have any questions or want to learn more? Reach out to us!
          </p>
          <a href="mailto:support@vortrex.com" className="text-green-400 text-lg sm:text-xl block mb-6">
            support@vortrex.com
          </a>
          <div className="flex justify-center flex-wrap gap-6">
            {['Facebook', 'Twitter', 'Telegram'].map((platform, i) => (
              <a
                key={i}
                href="#"
                className="text-gray-300 text-base sm:text-lg hover:text-green-400 transition"
              >
                {platform}
              </a>
            ))}
          </div>
        </div>
      </section>

    </div>
  );
};

export default HomePage;
